    <style type="text/css">
        body{font-family:Oxygen;font-weight:300;color:#fff;background-color:#363636;background:url(../img/background.jpg);background-attachment:fixed;background-position:center;background-repeat:no-repeat;background-size:cover;overflow-x:hidden;min-width:320px}html{width:100%;height:100%}h2{margin:0 0 30px;text-transform:uppercase;font-family:Fondamento;font-weight:700;letter-spacing:1px}a{color:#28c3ab}.navbar{margin-bottom:0;border-bottom:1px solid rgba(255,255,255,.3);background-color:rgba(0,0,0,.8)}.navbar-brand{font-weight:700;height:60px;padding:0;font-size:18px;line-height:1em}.logo-top{height:100px;position:relative;top:-20px}.navbar-custom ul.nav li a{font-size:12px;letter-spacing:1px;color:#fff;text-transform:uppercase;font-weight:700}.navbar-custom .nav li.active{outline:0;background-color:rgba(255,255,255,.2);border-radius:20px;outline:0;background-color:rgba(255,255,255,.2)}.navbar-toggle{padding:4px 6px;font-size:25px;color:#fff}#preloader{background:#fff;bottom:0;height:100%;left:0;position:fixed;right:0;top:0;width:100%;z-index:999}@keyframes shadowSpin{0%{box-shadow:10px -10px #39CCCC,-10px 10px #FFDC00}25%{box-shadow:10px 10px #39CCCC,-10px -10px #FFDC00}50%{box-shadow:-10px 10px #39CCCC,10px -10px #FFDC00}75%{box-shadow:-10px -10px #39CCCC,10px 10px #FFDC00}100%{box-shadow:10px -10px #39CCCC,-10px 10px #FFDC00}}#load{z-index:9999;background-color:#FF4136;opacity:.75;width:10px;height:10px;position:absolute;top:50%;margin:-5px auto 0;left:0;right:0;border-radius:0;border:5px solid #FF4136;box-shadow:10px 0 #39CCCC,10px 0 #01FF70;animation:shadowSpin 1s ease-in-out infinite}.home-section{padding-top:80px;display:block;z-index:120}.section-heading h2{font-size:40px;margin-bottom:0;text-shadow:5px 5px 10px rgba(0,0,0,0.7)}#form .success{width:100%!important;-moz-box-sizing:border-box;-webkit-box-sizing:border-box;-o-box-sizing:border-box;box-sizing:border-box}.btn{border-radius:0;text-transform:uppercase;font-family:Montserrat,sans-serif;font-weight:400;background-color:#fff;color:#000;border-radius:20px}.btn-dark{border:2px solid #000;color:#000}@media (min-width:767px){.navbar{padding:20px 0;border-bottom:0;letter-spacing:1px;background-color:rgba(0,0,0,.8)}}@media (max-width:768px){.logo-top{height:60px;top:0}}@media (max-width:480px){.navbar-custom .nav.navbar-nav{background-color:rgba(255,255,255,.4)}.navbar.navbar-custom.navbar-fixed-top{margin-bottom:30px}.logo-top{height:60px;top:0}}.clear{float:none!important;clear:both}.cl2{height:28px}a#toTop{margin-top:0}#toTop{display:none;text-decoration:none;position:fixed;bottom:14px;right:3%;overflow:hidden;width:50px;height:50px;border:solid #000;border-radius:20px;text-indent:100%;background:url(../img/to-top1.png) rgba(255,255,255,.8) no-repeat 50% 50%}
